import "../styles/about-us.scss"
import aboutImg from "../assets/img/about-image.svg"

const AboutUs = () => {
    return ( 
        <div id="about-section" className={"about-us d-flex justify-content-around p-5" + ((window.innerWidth < 992) ? " flex-wrap gap-5" : "")}    >
            <div className="about-text pe-5">
                <h1 className="fs-1">Illuminiamo i vostri desideri smart</h1>
                <h5 className="fst-italic fs-5">Alla ricerca di un modo per portare una nuova luce nella tua vita?</h5> 
                <p className="first-p fs-5">Ledzenith è qui per guidarti verso il futuro dell'illuminazione a <b>LED</b>.<br/>Siamo la tua destinazione per prodotti luminosi, <b>innovativi</b> ed <b>eco-sostenibili</b> che trasformeranno la tua <em>casa</em>, il tuo <em>ufficio</em> o il tuo <em>spazio commerciale</em> in un luogo di meravigliosa luminosità.</p>
                {/* <p>Presso LedZenith, crediamo che ogni ambiente meriti una luce di <b>alta qualità</b>. I nostri prodotti sono progettati per offrire una luce <em>brillante</em>, <em>uniforme</em> e <em>duratura</em>. Dimentica i vecchi sistemi di illuminazione e abbraccia il futuro con i nostri LED efficienti che <em>risparmiano energia</em>.</p> */}
                <a href="">
                    <span className="d-flex justify-content-center">Scopri di più</span>
                </a>
            </div>
            <div className="about-image d-flex align-items-center">
                <img src={aboutImg} alt="Illustrazione" className="about-image"/>
            </div>
        </div>
     );
}
 
export default AboutUs;
