import { useEffect, useState } from "react";
import workInProgress from "../assets/img/work-in-progress.png"

const WorkinProgress = () => {
    const [point, setPoint] = useState([])

    useEffect(() => {

        const interval = setInterval(() => {
            
            if (point.length === 3){
                setPoint([]);
            } else {
                setPoint(newArr => [...newArr, "."]);
            }
            }, 1000);

        return () => {
            clearInterval(interval);
        };

      }, [point]);

    
    return ( 
        <div className="vh-100 vw-100 work-in-progress d-flex justify-content-center align-items-center flex-column">
            <img className="vw-50" src={workInProgress} alt="work in progress" />
            <h1>Work In Progress{point}</h1>
        </div>
     );
}
 
export default WorkinProgress;
