import { useState } from "react";
import "../styles/customer-service.scss"

const CustomerService = () => {
    const [clicked, setClicked] = useState(0)

    function handleClick(i){
        setClicked(i)
    }

    return ( 
        <div>
            <h1>Contattaci per saperne di pi√π</h1>
            <div id="contact" className="contact-section vh-100 d-flex justify-content-center align-items-center position-relative">

                <div onClick={() => handleClick(0)} className={"contact-us bg-secondary " + ((clicked === 0) ? "z-1 " : "z-0 overlay" )}>
                    <div className={"w-100 h-100 z-2 bg-dark position-absolute opacity-75 " + ((clicked === 0) ? " visually-hidden" : "z-2 pointer" )}></div>
                    <h2>Chiedi informazioni tramite il <br/> modulo seguente</h2>
                    <button id="bg-color-video" className="button-about btn btn-primary fw-bold text-white">Send</button>
                </div>

                <div onClick={() => handleClick(1)} className={"reference bg-primary d-flex justify-content-center align-items-center " + ((clicked === 1) ? "z-1 " : "z-0 overlay" )}>
                    <div className={"w-100 h-100 bg-dark position-absolute opacity-75 " + ((clicked === 1) ? "visually-hidden" : "z-0 pointer" )}>
                    </div>
                    <h2 className="mb-4">Contattaci</h2>
                    <ul>
                        <li><i class="bi bi-telephone"></i>Telefono:&nbsp;&nbsp;<a href="#contact">1234567890</a></li>
                        <li><i class="bi bi-envelope"></i>Email:&nbsp;&nbsp;<a href="#contact">dumyemail@DanieleSere.evirgola</a></li>
                        <li><i class="bi bi-instagram"></i>Instagram:&nbsp;&nbsp;<a href="#contact">1234567890</a></li>
                        <li><i class="bi bi-facebook"></i>Facebook:&nbsp;&nbsp;<a href="#contact">1234567890</a></li>
                        <li><i class="bi bi-tiktok"></i>TikTok:&nbsp;&nbsp;<a href="#contact">1234567890</a></li>
                    </ul>
                </div>

            </div>
        </div>
     );
}
 
export default CustomerService;
